import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { URL_PATH } from "../config/urlPath";
import PlayCard from "./PlayCard";

export default function Play(props) {
  const [users, setUsers] = useState(props.location.state.users || []);
  const [selectedUsers, setSelectedUsers] = useState([]);
  console.log(
    "ðŸš€ ~ file: Play.js ~ line 9 ~ Play ~ selectedUsers",
    selectedUsers
  );
  const history = useHistory();

  const randomNumber = Math.floor(Math.random() * (9 - 1 + 1) + 1);

  useEffect(() => {
    let userList = users;
    let tempArray = [];
    userList.forEach((element) => {
      if (element.isSelected) {
        tempArray.push(element);
      }
    });
    setSelectedUsers(tempArray);
  }, [users]);

  return (
    <div className="play-area">
      <div className="my-auto">
        <div className="flex justify-center">
          {selectedUsers &&
            selectedUsers.map((user, i) => {
              return (
                i < 5 && (
                  <PlayCard
                    key={i}
                    user={user}
                    randomNumber={randomNumber}
                    increaseWinToUser={increaseWinToUser()}
                  />
                )
              );
            })}
        </div>
        <div>
          <div className="flex justify-center my-30">
            <div className="circle">
              <p>{randomNumber}</p>
            </div>
          </div>
        </div>
        <div className="flex justify-center">
          {selectedUsers &&
            selectedUsers.map((user, i) => {
              return (
                i > 4 && (
                  <PlayCard
                    key={i}
                    user={user}
                    randomNumber={randomNumber}
                    increaseWinToUser={increaseWinToUser()}
                  />
                )
              );
            })}
        </div>
      </div>
      <button
        className="back-btn"
        onClick={() => {
          history.push(URL_PATH.USER_LIST);
        }}
      >
        BACK
      </button>
    </div>
  );
}
