import React, { useEffect, useState } from "react";
import Table from "./Table";
import UserItem from "./UserItem";
import { useHistory } from "react-router";
import { URL_PATH } from "../config/urlPath";
import axios from "axios";
import { LIST_URL } from "../config";

export default function UserList() {
  const history = useHistory();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = () => {
    axios
      .get(LIST_URL)
      .then((response) => {
        setUsers(response)
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
        this.setState({ showMessage: true });
      })
      .finally(() => {
        this.setState({ showLoading: false });
      });
  };

  return (
    <div className="flex content">
      <div className="flex-side bg-gray">
        <div>
          <img
            style={{ width: "100%" }}
            src={require("../assets/images/dice.png")}
            alt=""
          />
          <div className="mx-1">
            <b className="text-muted">Playing 9</b>
            <UserItem />
          </div>
        </div>
        <div className="mx-1">
          <button
            className="start-btn"
            onClick={() => {
              history.push(URL_PATH.PLAY_AREA);
            }}
          >
            START
          </button>
        </div>
      </div>
      <div className="flex-main w-full">
        <div>
          <b className="text-blue">Select Playing 9</b>
          <div className="w-25 mt-4">
            <div className="flex">
              <div className="s-icon">
                <img
                  src={require("../assets/images/search.png")}
                  alt=""
                  height="12"
                  width="12"
                />
              </div>{" "}
              <input
                type="text"
                placeholder="Search Players"
                className="search"
              />
            </div>
          </div>
          <div className="mt-4">
            <Table />
          </div>
        </div>
      </div>
    </div>
  );
}
