import React, { Component } from "react";
import axios from "axios";
import URL_PATH from "../config/index";
import RecipeCard from "./RecipeCard";
import SearchBar from "./Searchbar";

class RecipeList extends Component {
  state = {
    showLoading: false,
    recipeList: [],
    responseError: "Unable to fetch recipes. Please try again",
    searchText: "",
    showMessage: false,
    filteredArray: [],
  };

  componentDidMount = () => {
    this.setState({ showLoading: true });
    axios
      .get(URL_PATH.LIST_URL)
      .then((response) => {
        this.setState({ recipeList: response.data });
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
        this.setState({ showMessage: true });
      })
      .finally(() => {
        this.setState({ showLoading: false });
      });
  };

  searchRecipe = (event) => {
    this.setState({ searchText: event.target.value });
    let recipes = this.state.recipeList;
    let filteredArray =
      recipes.length &&
      recipes.filter(
        (data) =>
          JSON.stringify(data)
            .toLowerCase()
            .indexOf(event.target.value.toLowerCase()) !== -1
      );
    this.setState({ filteredArray });
  };

  render() {
    const { recipeList, filteredArray } = this.state;
    return (
      <div className="content mx-auto h-full">
        <div className="flex content-center items-center justify-center h-full">
          <SearchBar
            searchRecipe={(e) => this.searchRecipe(e)}
            searchText={this.state.searchText}
          />
          {this.state.showMessage ? <p>{this.state.responseError}</p> : ""}
          {this.state.showLoading ? (
            "Loading..."
          ) : (
            <>
              <div className="heading">
                <div>
                  <span className="category">CATEGORY</span>
                  <h1>Pizza & Noodles</h1>
                </div>
              </div>
              <div class="container">
                <main class="grid">
                  {filteredArray.length
                    ? filteredArray.map((recipe, index) => {
                        return <RecipeCard key={index} recipe={recipe} />;
                      })
                    : recipeList.map((recipe, index) => {
                        return <RecipeCard key={index} recipe={recipe} />;
                      })}
                </main>
              </div>
            </>
          )}
        </div>
      </div>
    );
  }
}

export default RecipeList;
