import React, { useEffect, useState } from "react";
import Table from "./Table";
import UserItem from "./UserItem";
import { useHistory } from "react-router";
import { URL_PATH } from "../config/urlPath";
import axios from "axios";
import { LIST_URL } from "../config";
import { v4 as uuidv4 } from "uuid";

export default function UserList() {
  const history = useHistory();
  const [users, setUsers] = useState([]);
  const [loading, setShowLoading] = useState(false);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = () => {
    setShowLoading(true);
    axios
      .get(LIST_URL)
      .then((response) => {
        let userList = response.data;
        userList.forEach((element) => {
          element.isSelected = false;
          element.id = uuidv4();
        });
        setUsers(userList);
        console.log(
          "ðŸš€ ~ file: UserList.js ~ line 29 ~ .then ~ userList",
          userList
        );
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setShowLoading(false);
      });
  };

  const onCheck = (value, id) => {
    let isSelectedCount = 0;
    let userList = users;
    userList.forEach((element) => {
      if (element.isSelected) {
        isSelectedCount++;
      }
    });
    userList.forEach((element) => {
      if (element.id === id && isSelectedCount < 9) {
        element.isSelected = value;
      }
    });
    setUsers(JSON.parse(JSON.stringify(userList)));
  };

  const searchUser = (event) => {
    // this.setState({ searchText: event.target.value });
    // let recipes = this.state.recipeList;
    // let filteredArray =
    //   recipes.length &&
    //   recipes.filter(
    //     (data) =>
    //       JSON.stringify(data)
    //         .toLowerCase()
    //         .indexOf(event.target.value.toLowerCase()) !== -1
    //   );
    // this.setState({ filteredArray });
  };

  return (
    <div className="flex content">
      <div className="flex-side bg-gray">
        <div>
          <img
            style={{ width: "100%" }}
            src={require("../assets/images/dice.png")}
            alt=""
          />
          <div className="mx-1">
            <b className="text-muted">Playing 9</b>
            {users &&
              users.map((user, i) => {
                return user.isSelected && <UserItem key={i} user={user} />;
              })}
          </div>
        </div>
        <div className="mx-1">
          <button
            className="start-btn"
            onClick={() => onClickStart()}
          >
            START
          </button>
        </div>
      </div>
      <div className="flex-main w-full">
        <div>
          <b className="text-blue">Select Playing 9</b>
          <div className="w-25 mt-4">
            <div className="flex">
              <div className="s-icon">
                <img
                  src={require("../assets/images/search.png")}
                  alt=""
                  height="12"
                  width="12"
                />
              </div>{" "}
              <input
                type="text"
                placeholder="Search Players"
                className="search"
              />
            </div>
          </div>
          <div className="mt-4">
            {loading ? (
              "Loading..."
            ) : (
              <Table users={users} onCheck={(val, id) => onCheck(val, id)} />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
