import React from "react";
import { Link } from "react-router-dom";

const RecipeCard = ({ recipe }) => {
  return (
    <Link
      className="card"
      to={{ pathname: "/" + recipe.id, name: recipe.name }}
      style={{ textDecoration: "none", color: "black" }}
    >
      <div className="recipe-card">
        {recipe.label && <span className="recipe-label">{recipe.label}</span>}
        <img src={recipe.image} className="recipe-img" alt={recipe.name} />
        <div class="text">
          <div className="card-head-flex">
            <div className="card-heading">
              <h3>{recipe.name}</h3>
              <span className="price">$ {recipe.price}</span>
            </div>
            <div>
              <i class="fa fa-heart" style={{ color: "red" }}></i>
            </div>
          </div>
          <p className="description">{recipe.description}</p>
        </div>
      </div>
    </Link>
  );
};

export default RecipeCard;
